#悬赏NPC对话信息
def getRewardNpcTalk(iNo,iDialogPos):
	return gdReward.get(iNo,{}).get('dialog', [])[iDialogPos] if iDialogPos<len(gdReward.get(iNo,{}).get('dialog', [])) else ''

