import u
import task


'''
悬赏任务分组
{
	副本组:[任务编号1,任务编号2...],
	副本组:[任务编号1,任务编号2...]
}
'''

lData=[gdMain,gdDaily,gdBranch,gdReward, gdDegree]
gdLvByNo={}  	#{任务编号1:等级,任务编号2:等级}
gdNpcReward={}	#{悬赏NPC:[任务编号]}
gdTaskNpc={}	#{答题任务NCP:[任务编号]}


def init():
	for iNo,dValue in gdMain.iteritems():
		if dValue.get('event')!='PASS_FB':
			continue
		addTogdLvByNo(dValue,iNo)
	for iNo,dValue in gdDaily.iteritems():
		addTogdLvByNo(dValue,iNo)
	for iNo,dValue in gdDegree.iteritems():
		addTogdLvByNo(dValue,iNo)
	for iNo,dValue in gdReward.iteritems():
		gdNpcReward.setdefault(dValue['npc'],[]).append(iNo)
		if dValue['event']=="ANSWER_REWARD":
			gdTaskNpc.setdefault(dValue['targetBarrier'], []).append(iNo)	#此处的targetBarrier表示任务目标NPC
	for iKey,lRewards in gdNpcReward.iteritems():
		lRewards.sort()

def getNpcRewards(iNpcNo):
	return gdNpcReward.get(iNpcNo, [])

def addTogdLvByNo(dValue,iNo):
	lLimitType=dValue.get('limitType','').split('|')
	for sLimitType in lLimitType:
		if sLimitType!='LV':
			continue
		uValue=dValue['limitValue']
		if isinstance(uValue,(int,long)):
			gdLvByNo[iNo]=uValue
		else:
			gdLvByNo[iNo]=uValue[lLimitType.index(sLimitType)]

init()

def afterHotUpdate():
	task.init()#重新建立编号与模块的映射关系